import { Action } from '@ngrx/store';
import {Hero} from '../../Models/hero'

export const ADD_HERO='ADD_HERO';

export class AddHero implements Action {
    readonly type: string = ADD_HERO;
    constructor(public payload: Hero){
        payload = new Hero();
    }
}
