import {Hero, counter} from '../../Models/hero'

import * as HeroActions from '../Store/heroes.actions'

export interface AppState{
    HeroList: State;
}

export interface State{
    Heroes: Hero[];
    editedHero: Hero;
    editedHeroIndex: number;
    getHero: Function;
}

const initialState: State  = {
    Heroes: [
                new Hero('Maneesh','198','India',['technical analysis','extreme focus']),
                new Hero('Xin','176','China',['Power','Geometry'])
            ],
    editedHero: null,
    editedHeroIndex: -1,
    getHero: function(index: number = 0): Hero {
        return this.Heroes[index]
    }
}


export function heroesReducer(state: State = initialState, 
                              action: HeroActions.HeroActions) 
{

    switch (action.type) {

        case HeroActions.ADD_HERO:
             //state changes must be immutable, return a new object
            return {
                ...state,
                Heroes: [...state.Heroes, action.payload]
            }

        case HeroActions.ADD_HEROES:
            return {
                ...state, Heroes: [...state.Heroes, ...(action.payload as Hero[])]
            }

        case HeroActions.UPDATE_HERO:
            const hero = state.Heroes[action.payload.index];
            const updatedHero = {
                ...hero,
                ...action.payload.hero
            }
            const updatedHeroes = [...state.Heroes];

            console.log("updated hero")
            console.log(updatedHero)
            updatedHeroes[action.payload.index] = updatedHero;

            return {
                ...state, updatedHeroes
            }

        case HeroActions.DELETE_HERO:
            return {
                ...state,
               Heroes: state.Heroes.filter((hero, index) => {
                return index !== action.payload;
               })
            }
         default :
            return state

    }
}
