import { Component, OnInit} from '@angular/core';
import { FormGroup, FormArray, FormControl } from '@angular/forms';
import { Hero } from '../../Models/hero'
import { HeroesService } from 'src/app/service/heroes.service';

@Component({
  selector: 'app-update-hero',
  templateUrl: './update-hero.component.html',
  styleUrls: ['./update-hero.component.css']
})
export class UpdateHeroComponent implements OnInit {
    updateHeroForm: FormGroup;
    heroes: Hero[];
    hero: Hero;

    constructor(private hs: HeroesService) {}

  ngOnInit(): void {
        this.hero = this.hs.getHeroToEdit();
      console.log(hero)
  }

    onSubmit(){
      let controls = this.updateHeroForm;
      let newHero = new Hero(controls.get['name'], controls.get['height'], controls.get['origin'], controls.get['skills'])
      this.hs.updateHero(newHero)
    }
}
