import {Hero} from '../../Models/hero'

import * as HeroActions from '../Store/heroes.actions'

const initialState  = {
    Heroes: [
        new Hero('Maneesh','198','India',['technical analysis','extreme focus']),
        new Hero('Xin','176','China',['Power','Geometry'])
    ]
}

export function heroesReducer(state = initialState, 
                              action: HeroActions.HeroActions) 
{
    switch (action.type) {
        case HeroActions.ADD_HERO:
             //state changes must be immutable, return a new object
            return {
                ...state,
                Heroes: [...state.Heroes, action.payload]
            }
        case HeroActions.ADD_HEROES:
            return {
                ...state, Heroes: [...state.Heroes, ...(action.payload as Hero[])]
            }
        case HeroActions.UPDATE_HERO:
            const hero = state.Heroes[action.payload.index];
            const updatedHero = {
                ...hero,
                ...action.payload.hero
            }
            const updatedHeroes = [...state.Heroes];

            console.log("updated hero")
            console.log(updatedHero)
            updatedHeroes[action.payload.index] = updatedHero;

            return {
                ...state, updatedHeroes
            }
            default :
                return state

    }
}
