import { Component } from '@angular/core';
import {FormGroup, FormControl} from '@angular/forms'
import { Store } from '@ngrx/store';
import * as heroActions from '../Store/heroes.actions'
import {Hero} from '../../Models/hero'

@Component({
  selector: 'app-heroes',
  templateUrl: './heroes.component.html',
  styleUrls: ['./heroes.component.css']
})
export class HeroesComponent {

  hero: Hero;
  newHeroForm: FormGroup;

  constructor(private store: Store<{heroesList: {heroes: Hero[]}}>) {

    this.hero = new Hero();

    this.newHeroForm = new FormGroup(
      {name: new FormControl(null),
       height: new FormControl(null),
       origin: new FormControl(null),
       skills: new FormControl(null)}
    )
  }

  onSubmit(){
    let form = this.newHeroForm.value;
    let hero = new Hero( form.name, form.height, form.origin, form.skills)
    console.log(hero)
    this.store.dispatch(new heroActions.AddHero(hero))
  }

}
<p>heroes works!!</p>
<div>
    <div>
        <button routerLink="/display">display</button>
    </div>
    <div>
        <form [formGroup]="newHeroForm" (ngSubmit)="onSubmit()">
            <div>
                <label>Name</label>
                <input formControlName="name" [(ngModel)]="hero.name">
            </div>
            <div>
                <label>Height</label>
                <input formControlName="height" [(ngModel)]="hero.height">
            </div>
            <div>
                <label>Origin</label>
                <input formControlName="origin" [(ngModel)]="hero.origin">
            </div>
            <div>
                <label>Skills</label>
                <input formControlName="skills" [(ngModel)]="hero.skills">
            </div>
            <button>Submit</button>
        </form>
    </div> 
</div>
