import { Component } from '@angular/core';
import {Hero} from '../../Models/hero'
import {Observable, Subscription } from 'rxjs';
import { Store, Action } from '@ngrx/store';
import * as heroActions from '../Store/heroes.actions'

@Component({
  selector: 'app-display',
  templateUrl: './display.component.html',
  styleUrls: ['./display.component.css']
})
export class DisplayComponent {
  constructor(private store: Store<{heroList: {heroes: Hero[]}}>) {console.log("display comp init")}

  heroes: Hero[] = [];

ngOnInit(): void {

  this.store.select('heroList').subscribe( 
    {next : (hero: {heroes: Hero[]}) => this.heroes = hero.heroes,
     error: (error) => console.log(error)} 
  )

}

ngOnDestroy(): void {
console.log("destroyed display component")
}


}
